import 'dart:convert';import 'package:shared_preferences/shared_preferences.dart';import '../../Model/user.dart';class UserPreferences {  static late SharedPreferences _preferences;  static const _keyUser = 'user';  static const myUser = Users(    uid: '0000',    imagePath:        'https://images.unsplash.com/photo-1554151228-14d9def656e4?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=333&q=80',    name: '爆釣 太郎',    nickName: '釣り神',    about: '初めまして！',    areas: '世界の海、川',    isDarkMode: false,  );  static Future init() async =>      _preferences = await SharedPreferences.getInstance();  static Future setUser(Users user) async {    final json = jsonEncode(user.toJson());    await _preferences.setString(_keyUser, json);  }  static Users getUser() {    final json = _preferences.getString(_keyUser);    return json == null ? myUser : Users.fromJson(jsonDecode(json));  }}