class Users {  final String uid;  final String imagePath;  final String name;  final String nickName;  final String about;  final String areas;  final bool isDarkMode;  const Users({    required this.uid,    required this.imagePath,    required this.name,    required this.nickName,    required this.about,    required this.areas,    required this.isDarkMode,  });  Users copy({    String? uid,    String? imagePath,    String? name,    String? nickName,    String? about,    String? areas,    bool? isDarkMode,  }) =>      Users(        uid: uid ?? this.uid,        imagePath: imagePath ?? this.imagePath,        name: name ?? this.name,        nickName: nickName ?? this.nickName,        about: about ?? this.about,        areas: areas ?? this.areas,        isDarkMode: isDarkMode ?? this.isDarkMode,      );  static Users fromJson(Map<String, dynamic> json) => Users(        uid: json['uid'],        imagePath: json['imagePath'],        name: json['name'],        nickName: json['nickName'],        about: json['about'],        areas: json['areas'],        isDarkMode: json['isDarkMode'],      );  Map<String, dynamic> toJson() => {        'uid': uid,        'imagePath': imagePath,        'name': name,        'nickName': nickName,        'about': about,        'areas': areas,        'isDarkMode': isDarkMode,      };}