import 'package:animated_theme_switcher/animated_theme_switcher.dart';import 'package:flutter/material.dart';import 'package:get_fish/Model/user.dart';import '../../Common/parts/user_preference.dart';import '../../Common/widget/appbar_widget.dart';import '../../Common/widget/button_widget,dart.dart';import '../../Common/widget/profile_widget.dart';import 'edit_user_profile.dart';class ProfilePage extends StatefulWidget {  const ProfilePage({Key? key}) : super(key: key);  @override  _ProfilePageState createState() => _ProfilePageState();}class _ProfilePageState extends State<ProfilePage> {  @override  Widget build(BuildContext context) {    final user = UserPreferences.getUser();    print('-----------user--------');    print(user.name);    return ThemeSwitchingArea(      child: Builder(        builder: (context) => Scaffold(          appBar: buildAppBar(context),          body: ListView(            physics: BouncingScrollPhysics(),            children: [              ProfileWidget(                imagePath: user.imagePath,                onClicked: () async {                  await Navigator.of(context).push(                    MaterialPageRoute(                        builder: (context) => const EditProfilePage()),                  );                  setState(() {});                },              ),              const SizedBox(height: 24),              buildName(user),              //const SizedBox(height: 24),              //Center(child: buildUpgradeButton()),              //const SizedBox(height: 24),              //NumbersWidget(),              const SizedBox(height: 24),              buildAbout(user),            ],          ),        ),      ),    );  }  Widget buildName(Users user) => Column(        children: [          Text(            user.name,            style: TextStyle(fontWeight: FontWeight.bold, fontSize: 24),          ),          const SizedBox(height: 4),          Text(            user.nickName,            style: TextStyle(color: Colors.grey),          )        ],      );  // Widget buildUpgradeButton() => ButtonWidget(  //       text: 'Upgrade To PRO',  //       onClicked: () {},  //     );  Widget buildAbout(Users user) => Container(        padding: EdgeInsets.symmetric(horizontal: 48),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Text(              'よく行くエリア',              style: const TextStyle(fontSize: 24, fontWeight: FontWeight.bold),            ),            const SizedBox(height: 16),            Text(              user.areas,              style: TextStyle(fontSize: 16, height: 1.4),            ),            const SizedBox(height: 16),            Text(              '自己紹介',              style: const TextStyle(fontSize: 24, fontWeight: FontWeight.bold),            ),            const SizedBox(height: 16),            Text(              user.about,              style: TextStyle(fontSize: 16, height: 1.4),            ),          ],        ),      );}